/*
.align
.global SomeLabel_len
.int 1234
.global SomeLabel
.byte blah,blah,blah,blah...
*/


#include <stdio.h>
#include <stdlib.h>
#include <ctype.h>

/* strnident() *************************
   Print the closest valid C identifier to a given word.
*/
void strnident(FILE *fout, const char *src)
{
  char got_first = 0;

  while(*src != 0)
    {
      int s = *src++;

      /* initial digit  */
      if(isdigit(s) && !got_first)
	fputc('_', fout);  /* stick a '_' before an initial digit */

      /* convert only out-of-range characters */
      if(!isalpha(s) && !isdigit(s) && (s != '_'))
	{
	  if(s == '-' || s == '.' || s == '/')
	    s = '_';
	  else
	    s = 0;
	}

      if(s)
	{
	  fputc(s, fout);
	  got_first = 1;
	}
    }
}


int main(int argc, char **argv)
{
  FILE *fin;
  size_t filelen;
  int linelen;
  int arg;

  if(argc < 2)
    {
      fputs("bin2s - convert binary files to assembly language\n"
            "typical usage: bin2s foo.bin bar.bin baz.bin > foo.s\n", stderr);
      return 1;
    }

  for(arg = 1; arg < argc; arg++)
    {
      fin = fopen(argv[arg], "rb");
      if(!fin)
	{
	  fputs("bin2s: could not open ", stderr);
          perror(argv[arg]);
	  return 1;
	}

      fseek(fin, 0, SEEK_END);
      filelen = ftell(fin);
      rewind(fin);

      if(filelen == 0)
	{
	  fclose(fin);
          fprintf(stderr, "bin2s: warning: skipping empty file %s\n",
                  argv[arg]);
          continue;
	}


      /* Generate the prolog for each included file.  It has two purposes:
         1. provide length info, and 2. align to 32-bit boundary */
      fputs("/* Generated by BIN2S - please don't edit directly */\n"
            ".section .rodata\n"
            ".balign 4\n"
            ".global ", stdout);
      strnident(stdout, argv[arg]);
      fputs("_len\n", stdout);
      strnident(stdout, argv[arg]);
      printf("_len: .int %lu\n.global ", (unsigned long)filelen);
      strnident(stdout, argv[arg]);
      fputs("\n", stdout);
      strnident(stdout, argv[arg]);
      fputs(":\n.byte ", stdout);

      linelen = 0;

      while(filelen > 0)
	{
	  unsigned char c = fgetc(fin);

	  printf("%3u", (unsigned int)c);
	  filelen--;

	  /* don't put a comma after the last item */
	  if(filelen)
	    {
	      /* break after every 16th number */
	      if(++linelen >= 16)
		{
		  linelen = 0;
		  fputs("\n.byte ", stdout);
		}
              else
                {
                  fputc(',', stdout);
                }

	    }
	}
      fputc('\n', stdout);

      fclose(fin);
    }
  return 0;
}

